CREATE OR REPLACE PROCEDURE daily_price_update_9f7c2a8b()
LANGUAGE plpgsql
AS $$
BEGIN
PERFORM net.http_post(
url:='https://htvlwhisjpdagqkqnpxg.supabase.co/functions/v1/cron-jobs-maxiconsumo',
headers:=jsonb_build_object('Content-Type', 'application/json', 'Authorization', 'Bearer ' || current_setting('app.service_role_key')),
body:=jsonb_build_object(
    'action', 'execute',
    'job_id', 'daily-price-update',
    'parameters', jsonb_build_object(
        'scraping_mode', 'full_catalog',
        'price_change_threshold', 2,
        'notifications_enabled', true
    )
),
timeout_milliseconds:=300000
);
COMMIT;
END;
$$;

SELECT cron.schedule('daily_price_update', '0 2 * * *', 'CALL daily_price_update_9f7c2a8b()');
