CREATE OR REPLACE PROCEDURE realtime_alerts_5a9b4c2d()
LANGUAGE plpgsql
AS $$
BEGIN
PERFORM net.http_post(
url:='https://htvlwhisjpdagqkqnpxg.supabase.co/functions/v1/cron-jobs-maxiconsumo',
headers:=jsonb_build_object('Content-Type', 'application/json', 'Authorization', 'Bearer ' || current_setting('app.service_role_key')),
body:=jsonb_build_object(
    'action', 'execute',
    'job_id', 'realtime-alerts',
    'parameters', jsonb_build_object(
        'alert_threshold', 15,
        'check_frequency', 15,
        'critical_products_only', true,
        'instant_notifications', true,
        'escalation_enabled', true
    )
),
timeout_milliseconds:=120000
);
COMMIT;
END;
$$;

SELECT cron.schedule('realtime_alerts', '*/15 * * * *', 'CALL realtime_alerts_5a9b4c2d()');
