CREATE OR REPLACE PROCEDURE weekly_trend_analysis_3d8e5f7c()
LANGUAGE plpgsql
AS $$
BEGIN
PERFORM net.http_post(
url:='https://htvlwhisjpdagqkqnpxg.supabase.co/functions/v1/cron-jobs-maxiconsumo',
headers:=jsonb_build_object('Content-Type', 'application/json', 'Authorization', 'Bearer ' || current_setting('app.service_role_key')),
body:=jsonb_build_object(
    'action', 'execute',
    'job_id', 'weekly-trend-analysis',
    'parameters', jsonb_build_object(
        'analysis_period', 'weekly',
        'ml_predictions', true,
        'seasonal_patterns', true,
        'generate_reports', true
    )
),
timeout_milliseconds:=600000
);
COMMIT;
END;
$$;

SELECT cron.schedule('weekly_trend_analysis', '0 3 * * 0', 'CALL weekly_trend_analysis_3d8e5f7c()');
